const productImageNextBtn=document.querySelector(".productDetail__container__imgs--next"),productImageBackBtn=document.querySelector(".productDetail__container__imgs--back"),productList=document.querySelector(".productDetail__container__imgs__list"),productImgs=document.querySelectorAll(".productDetail__container__imgs__list__img").length;let count=0;productImageNextBtn.addEventListener("click",(t=>{t.preventDefault(),count<productImgs-1&&count++,productList.style.transform=`translatex(-${count}00%)`})),productImageBackBtn.addEventListener("click",(t=>{t.preventDefault(),count>0&&count--,productList.style.transform=`translatex(-${count}00%)`}));